import React from 'react';
import ImgComp from './ImgComp';
import '../../Styles/recycler.css';

import CircularNode from '../../DataStructures/CircularNode';

class Recycler extends React.Component{
  constructor(props){
    super(props);
    // turn the array of images into a doubly circular linked list
    let tempMid = this.createList( this.props.sliderArr );
    this.state = {
      // pointer to the middle item
      mid: tempMid
    };
  }

  createList(arr){
    // create ptr
    let head = new CircularNode(null,null,arr[0]);
    let ptr = head;
    // loop through them
    let i = 1;
    for( i = 1;i < (arr.length - 1); i++){
      let temp = new CircularNode(null, ptr, arr[i]);
      ptr.next = temp;
      ptr = temp;
    }

    // create the tail
    CircularNode.addTail(head, arr[i]);
    ptr.next = head.prev;
    return head.next;
  }

  render(){

    const goLeft = () => {
      this.setState({mid: this.state.mid.prev})
    }
    const goRight = () => {
      this.setState({mid: this.state.mid.next})
    }
    return (
      <div>
        <div>
          <ImgComp src={this.state.mid.prev.i} />
          <ImgComp src={this.state.mid.i} />
          <ImgComp src={this.state.mid.next.i} />
        </div>
        <div style={{display:'flex',justifyContent:'center',alignItems:'center'}}>
          <button style={{background:'none',border:'none',outline:'none'}} onClick={goLeft}>
            <i class="fas fa-arrow-left"></i>
          </button>
          <button style={{background:'none',border:'none',outline:'none'}} onClick={goRight}>
            <i class="fas fa-arrow-right"></i>
          </button>
        </div>
      </div>
    );
  }
}

export default Recycler;
