import React from 'react';

import './post.css';

class Post extends React.Component{

  constructor(props){
    super(props);
  }

  render(){
    return(
      <div className="postContainer">
        <img src={this.props.image} alt="post image"></img>
        <h1>{this.props.title}</h1>
        <h2>{this.props.date}</h2>
        <p>{this.props.description}</p>

      </div>
    );
  }
}

export default Post;
