import React from 'react';
import ImgComp from './ImgComp';

import CircularNode from '../../DataStructures/CircularNode';

class Recycler extends React.Component{
  constructor(props){
    super(props);
    let tempMid = this.createList( this.props.sliderArr );
    this.state = {
      mid: tempMid
    };
  }

  createList(arr){
    // create ptr
    let head = new CircularNode(null,null,arr[0]);
    let ptr = head;
    // loop through them
    let i = 1;
    for( i = 1;i < (arr.length - 1); i++){
      let temp = new CircularNode(null, ptr, arr[i]);
      ptr.next = temp;
      ptr = temp;
    }

    // create the tail
    CircularNode.addTail(head, arr[i]);
    ptr.next = head.prev;
    return head.next;
  }

  render(){

    const goLeft = () => {
      this.setState({mid: this.state.mid.prev})
    }
    const goRight = () => {
      this.setState({mid: this.state.mid.next})
    }
    return (
      <div>
        <div style={{display:'flex',align:'center'}}>
          <button onClick={goLeft}>Left</button>
          <button onClick={goRight}>Right</button>
        </div>
        <div>
          <ImgComp src={this.state.mid.prev.i} />
          <ImgComp src={this.state.mid.i} />
          <ImgComp src={this.state.mid.next.i} />
        </div>
      </div>
    );
  }
}

export default Recycler;
