import React from 'react';
import ImgComp from './ImgComp';
import '../../Styles/recycler.css';

import styled, { keyframes } from 'styled-components';
import {slideInLeft, slideInRight} from 'react-animations';

import CircularNode from '../../DataStructures/CircularNode';

const SlideLeft = styled.span`animation: 0.5s ${keyframes`${slideInLeft}`}`;
const SlideRight = styled.span`animation: 0.5s ${keyframes`${slideInRight}`}`;

class Recycler extends React.Component{
  constructor(props){
    super(props);
    // turn the array of images into a doubly circular linked list
    let tempMid = this.createList( this.props.sliderArr );
    // bind the two onclick functions
    this.goLeft = this.goLeft.bind(this);
    this.goRight = this.goRight.bind(this);
    this.returnSlides = this.returnSlides.bind(this);

    this.state = {
      // pointer to the middle item
      mid: tempMid,
      transition: 0
    };
  }

  createList(arr){
    // create ptr
    let head = new CircularNode(null,null,arr[0]);
    let ptr = head;
    // loop through them
    let i = 1;
    for( i = 1;i < (arr.length - 1); i++){
      let temp = new CircularNode(null, ptr, arr[i]);
      ptr.next = temp;
      ptr = temp;
    }

    // create the tail
    CircularNode.addTail(head, arr[i]);
    ptr.next = head.prev;
    return head.next;
  }

  goLeft(){ this.setState({mid: this.state.mid.prev, transition: -1}) }
  goRight(){ this.setState({mid: this.state.mid.next, transition: 1}) }

  returnSlides(){
    let imageStyles={
      width: 33+ "%",
      height: "auto"
    };


    return(
      <div>
        <SlideLeft style={imageStyles}>
          <ImgComp src={this.state.mid.prev.i} />
        </SlideLeft>
        <SlideLeft style={imageStyles}>
          <ImgComp src={this.state.mid.i} />
        </SlideLeft>
        <SlideLeft style={imageStyles}>
          <ImgComp src={this.state.mid.next.i} />
        </SlideLeft >
      </div>
    )
  }

  render(){
    return (
      <div className="recycler">

        {this.returnSlides()}

        <div className='buttonContainer'>
          <button className='buttonStyle' onClick={this.goLeft}>
            <i class="fas fa-arrow-left"></i>
          </button>
          <button className='buttonStyle' onClick={this.goRight}>
            <i class="fas fa-arrow-right"></i>
          </button>
        </div>
      </div>
    );
  }
}

export default Recycler;
